// Parse Snyk API response and format for processing
const snykResponse = $input.first().json;

const arrayIssue = JSON.parse(snykResponse.data);
console.log(arrayIssue.data)
// if (!arrayIssue || !Array.isArray(arrayIssue)) {
//   console.log('No issues found or invalid response from Snyk');
//   return [];
// }

// Transform Snyk issues to our internal format
const findings = arrayIssue.data
  .filter(issue => issue.attributes?.status === 'open')
  .slice(0, 10) // Limit to 10 issues per run
  .map(issue => {
    const attrs = issue.attributes || {};
    const problems = attrs.problems || [];
    const firstProblem = problems[0] || {};

    // Map Snyk severity to our format
    const severityMap = {
      'critical': 'critical',
      'high': 'high',
      'medium': 'medium',
      'low': 'low'
    };

    return {
      id: issue.id || `SNYK-${Date.now()}`,
      title: attrs.title || 'Security Issue',
      severity: severityMap[attrs.severity] || 'medium',
      description: attrs.description || 'Security vulnerability detected by Snyk',
      affected_file: firstProblem.source?.file_path || 'unknown',
      line_number: firstProblem.source?.start_line || 0,
      cwe_id: attrs.classes?.map(c => c.id).find(id => id?.startsWith('CWE-')) || 'CWE-unknown',
      package_name: attrs.key || 'unknown',
      vulnerable_code: firstProblem.source?.code || '',
      snyk_url: `https://app.snyk.io/org/${attrs.organization_id}/project/${attrs.project_id}`,
      repository: attrs.project_name || 'unknown-repo',
      scan_date: new Date().toISOString(),
      scan_tool: 'Snyk Code'
    };
  });

if (findings.length === 0) {
  console.log('No open issues to process');
  return [];
}

return findings.map(finding => ({ json: finding }));

// Parse AI response and format for database storage
const response = $input.first().json;
// AI node output can be in different formats
const aiContent = response.output || response.text || response.response || '';

let analysisResult;
try {
  // Try to parse JSON from AI response
  // Remove markdown code blocks if present
  const cleanContent = aiContent.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  analysisResult = JSON.parse(cleanContent);
} catch (error) {
  // If JSON parsing fails, extract information manually
  console.log('AI response is not valid JSON, extracting manually...');

  const tmpJson = $input.first().json;
  const originalFinding = JSON.parse(tmpJson.message.content)
  console.log(originalFinding)

  // Map severity to PCI requirement
  let pciRequirement;
  if (originalFinding.cwe_id === 'CWE-89') {
    pciRequirement = '6.5.1'; // SQL Injection
  } else if (originalFinding.cwe_id === 'CWE-79') {
    pciRequirement = '6.5.7'; // XSS
  } else if (originalFinding.cwe_id === 'CWE-338') {
    pciRequirement = '6.5.3'; // Insecure cryptographic storage
  } else {
    pciRequirement = '6.5.6'; // Other high/critical vulnerabilities
  }

  // Create structured result
  analysisResult = {
    finding_id: `PCI-${Date.now()}-${Math.random().toString(36).substr(2, 5)}`,
    severity: originalFinding.severity,
    pci_requirement: pciRequirement,
    cwe_id: originalFinding.cwe_id,
    title: originalFinding.title,
    description: originalFinding.description,
    fix_suggestion: getFix(originalFinding.cwe_id),
    risk_score: getRiskScore(originalFinding.severity),
    evidence: {
      scan_tool: 'Snyk Code',
      scan_date: new Date().toISOString(),
      repo: originalFinding.evidence.repo,
      file: originalFinding.evidence.file,
      line: originalFinding.evidence.line
    }
  };
}

// Helper functions
function getFix(cweId) {
  const fixes = {
    'CWE-89': 'Use parameterized queries: const query = "SELECT * FROM payments WHERE user_id = ?"; db.query(query, [userId]);',
    'CWE-79': 'Encode output: const clean = DOMPurify.sanitize(userInput); res.send(`<h1>Error: ${clean}</h1>`);',
    'CWE-338': 'Use crypto.randomBytes(): const crypto = require("crypto"); const token = crypto.randomBytes(32).toString("hex");'
  };
  return fixes[cweId] || 'Review code for security best practices and apply appropriate fixes.';
}

function getRiskScore(severity) {
  const scores = { critical: 9, high: 7, medium: 5, low: 3 };
  return scores[severity] || 5;
}

// Generate evidence package markdown
const evidenceDocument = `# Security Finding Evidence Package\n\n**Finding ID:** ${analysisResult.finding_id}\n**Severity:** ${analysisResult.severity}\n**PCI DSS Requirement:** ${analysisResult.pci_requirement}\n**Risk Score:** ${analysisResult.risk_score}/10\n\n## Vulnerability Details\n- **Repository:** ${analysisResult.evidence.repo}\n- **File:** ${analysisResult.evidence.file}:${analysisResult.evidence.line}\n- **CWE:** ${analysisResult.cwe_id}\n- **Scan Tool:** ${analysisResult.evidence.scan_tool}\n- **Discovery Date:** ${analysisResult.evidence.scan_date}\n\n## Description\n${analysisResult.description}\n\n## Recommended Fix\n\`\`\`\n${analysisResult.fix_suggestion}\n\`\`\`\n\n## Compliance Impact\nThis vulnerability affects PCI DSS ${analysisResult.pci_requirement} compliance and must be addressed according to risk-based prioritization.\n\n---\n*Generated by DOKU PCI Compliance Automation*\n*Timestamp: ${new Date().toISOString()}*`;

// Return formatted result
return {
  json: {
    ...analysisResult,
    evidence_document: evidenceDocument
  }
};

// Store finding metadata to knowledge base for ChatBot learning
const finding = $input.first().json;

// Create searchable content
const searchText = `${finding.title} - ${finding.description} - Fix: ${finding.fix_suggestion}`;

// Extract keywords for search
const keywords = searchText
  .toLowerCase()
  .replace(/[^a-z0-9\s]/g, ' ')
  .split(/\s+/)
  .filter(word => word.length > 2)
  .filter((word, index, arr) => arr.indexOf(word) === index)
  .slice(0, 15);

const keywordArray = keywords.map(k => k.replace(/'/g, "''")).join("','");

return {
  json: {
    finding_id: finding.finding_id,
    title: `Security Finding: ${finding.title}`,
    content: searchText.replace(/'/g, "''"),
    doc_type: 'evidence',
    keywords_array: `ARRAY['${keywordArray}']`,
    source_type: 'evidence_package',
    metadata_json: JSON.stringify({
      severity: finding.severity,
      pci_requirement: finding.pci_requirement,
      cwe_id: finding.cwe_id,
      repo: finding.repo_name,
      file: finding.affected_file,
      line: finding.line_number,
      risk_score: finding.risk_score
    })
  }
};

